for (int i = 0; i < 42; i++) // not recommended
    foo();
for (int i = 0; i < 42; i++) { // preferred approach
    foo();
}

// this is OK
if (foo) x++;
// but this is not
if (foo)
    x = x+1;
else
    x = x-1;


if (foo)    // not recommended
    x++;
if (foo) {  // preferred approach
    x++;
}

